// src/components/Dashboard/Dashboard.jsx
import React, { useState } from "react";
import {
  useNavigate,
  BrowserRouter,
  Routes,
  Route,
  useLocation,
} from "react-router-dom";
import "./LetterShell.css";
import { useTranslation } from "react-i18next";
import filePickerImg from "../../assets/filepicker.png";

function LetterShell() {
  const navigate = useNavigate();
  const { t } = useTranslation();
  const { pathname } = useLocation();

  const handleBulkLetter = () => {
    navigate("/new-letter");
  };

  const handleSingleLetter = () => {
    navigate("/new-letter");
  };

  const handleCancel = () => {
    navigate("/new-letter");
  };

  return (
    <div className="letter-shell-container">
      <div className="letter-title-area">
        <div className="letter-title">Send new letter</div>
        <div class="stepper">
          <div class="line"></div>
          <div class="steps">
            <div class="step active">1</div>
            <div class="step">2</div>
            <div class="step">3</div>
          </div>
        </div>
      </div>
      <div className="letter-main-area">
        {/* <img src={filePickerImg} /> */}
        <div className="form-column">
            <h2>Attachment</h2>
            <div className="form-group">
              <input
                type="file"
                id="file-upload"
                accept=".pdf"
                onChange={handleFileUpload}
                style={{ display: "none" }}
              />
              <label htmlFor="file-upload" className="upload-button">
                ðŸ“¤ UPLOAD FILE
              </label>
              {!uploadedFile && <span className="file-status">No file</span>}
              {errors.file && (
                <span className="error-message">{errors.file}</span>
              )}
            </div>

            <h2>Return Address</h2>
            <div className="form-group">
              <input
                type="text"
                name="returnFirstName"
                placeholder="First name"
                value={formData.returnFirstName}
                onChange={handleChange}
                className={errors.returnFirstName ? "error" : ""}
              />
              {errors.returnFirstName && (
                <span className="error-message">{errors.returnFirstName}</span>
              )}
            </div>

            <div className="form-group">
              <input
                type="text"
                name="returnLastName"
                placeholder="Last name"
                value={formData.returnLastName}
                onChange={handleChange}
                className={errors.returnLastName ? "error" : ""}
              />
              {errors.returnLastName && (
                <span className="error-message">{errors.returnLastName}</span>
              )}
            </div>

            <div className="form-group">
              <input
                type="text"
                name="returnCompany"
                placeholder="Company"
                value={formData.returnCompany}
                onChange={handleChange}
              />
            </div>

            <div className="form-row">
              <div className="form-group flex-2">
                <input
                  type="text"
                  name="returnStreet"
                  placeholder="Street"
                  value={formData.returnStreet}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group flex-1">
                <input
                  type="text"
                  name="returnHouseNo"
                  placeholder="House no."
                  value={formData.returnHouseNo}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group flex-1">
                <input
                  type="text"
                  name="returnFlatNo"
                  placeholder="Flat no."
                  value={formData.returnFlatNo}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group flex-2">
                <input
                  type="text"
                  name="returnCity"
                  placeholder="City"
                  value={formData.returnCity}
                  onChange={handleChange}
                  className={errors.returnCity ? "error" : ""}
                />
                {errors.returnCity && (
                  <span className="error-message">{errors.returnCity}</span>
                )}
              </div>
              <div className="form-group flex-1">
                <input
                  type="text"
                  name="returnZipCode"
                  placeholder="ZIP code"
                  value={formData.returnZipCode}
                  onChange={handleChange}
                  className={errors.returnZipCode ? "error" : ""}
                />
                {errors.returnZipCode && (
                  <span className="error-message">{errors.returnZipCode}</span>
                )}
              </div>
            </div>

            <div className="form-actions-inline">
              <button className="btn-cancel" onClick={handleCancel}>
                CANCEL
              </button>
              <button className="btn-save" onClick={handleSave}>
                SAVE
              </button>
            </div>
          </div>
      </div>
      <div className="letter-options-area">
        <button className="btn-new-letter" onClick={handleBulkLetter}>
          <span className="letter-icon">
            <i class="fa-solid fa-envelopes-bulk"></i>
          </span>
          SEND LETTERS IN BULK
        </button>
        <button className="btn-new-letter" onClick={handleSingleLetter}>
          <span className="letter-icon">
            <i class="fa-solid fa-envelope"></i>
          </span>
          SEND LETTER
        </button>
        <button className="btn-new-letter btn-cancel" onClick={handleCancel}>
          <span className="letter-icon">
            <i class="fa-solid fa-ban"></i>
          </span>
          CANCEL
        </button>
      </div>
    </div>
  );
}

export default LetterShell;
