import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./BulkLetter.css";

const BulkLetter = () => {
  const navigate = useNavigate();

  // Form state
  const [formData, setFormData] = useState({
    // Recipient
    recipientFirstName: "",
    recipientLastName: "",
    recipientCompany: "",
    recipientStreet: "",
    recipientHouseNo: "",
    recipientFlatNo: "",
    recipientCity: "",
    recipientZipCode: "",
    recipientCountry: "",

    // Return Address
    returnFirstName: "",
    returnLastName: "",
    returnCompany: "",
    returnStreet: "",
    returnHouseNo: "",
    returnFlatNo: "",
    returnCity: "",
    returnZipCode: "",

    // Mail Options
    mailType: "standard",

    // Letter Language
    language: "",
  });

  const [uploadedFile, setUploadedFile] = useState(null);
  const [filePreview, setFilePreview] = useState(null);
  const [errors, setErrors] = useState({});
  const [currentStep, setCurrentStep] = useState(1);

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));

    // Clear error for this field
    if (errors[name]) {
      setErrors((prev) => ({
        ...prev,
        [name]: "",
      }));
    }
  };

  // Handle file upload
  const handleFileUpload = (e) => {
    const file = e.target.files[0];

    if (file) {
      // Validate file type
      if (file.type !== "application/pdf") {
        setErrors((prev) => ({
          ...prev,
          file: "Only PDF files are allowed",
        }));
        return;
      }

      // Validate file size (max 10MB)
      if (file.size > 10 * 1024 * 1024) {
        setErrors((prev) => ({
          ...prev,
          file: "File size must be less than 10MB",
        }));
        return;
      }

      setUploadedFile(file);
      setErrors((prev) => ({
        ...prev,
        file: "",
      }));

      // Create preview URL for PDF
      const fileURL = URL.createObjectURL(file);
      setFilePreview(fileURL);
    }
  };

  // Remove uploaded file
  const handleRemoveFile = () => {
    setUploadedFile(null);
    setFilePreview(null);
    // Reset file input
    const fileInput = document.getElementById("file-upload");
    if (fileInput) fileInput.value = "";
  };

  // Validate form
  const validateForm = () => {
    const newErrors = {};

    // Recipient validation
    if (!formData.recipientFirstName.trim()) {
      newErrors.recipientFirstName = "First name is required";
    }
    if (!formData.recipientLastName.trim()) {
      newErrors.recipientLastName = "Last name is required";
    }
    if (!formData.recipientStreet.trim()) {
      newErrors.recipientStreet = "Street is required";
    }
    if (!formData.recipientCity.trim()) {
      newErrors.recipientCity = "City is required";
    }
    if (!formData.recipientZipCode.trim()) {
      newErrors.recipientZipCode = "ZIP code is required";
    }
    if (!formData.recipientCountry.trim()) {
      newErrors.recipientCountry = "Country is required";
    }

    // Return Address validation
    if (!formData.returnFirstName.trim()) {
      newErrors.returnFirstName = "Return first name is required";
    }
    if (!formData.returnLastName.trim()) {
      newErrors.returnLastName = "Return last name is required";
    }
    if (!formData.returnCity.trim()) {
      newErrors.returnCity = "Return city is required";
    }
    if (!formData.returnZipCode.trim()) {
      newErrors.returnZipCode = "Return ZIP code is required";
    }

    // File validation
    if (!uploadedFile) {
      newErrors.file = "Please upload a PDF file";
    }

    // Language validation
    if (!formData.language) {
      newErrors.language = "Please select a language";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle cancel
  const handleCancel = () => {
    navigate("/");
  };

  // Handle save (return to dashboard without submitting)
  const handleSave = () => {
    console.log("Form saved (draft)");
    navigate("/");
  };

  // Handle next/submit
  const handleNext = async () => {
    if (validateForm()) {
      // Prepare API request body
      const apiRequestBody = {
        recipient: {
          firstName: formData.recipientFirstName,
          lastName: formData.recipientLastName,
          company: formData.recipientCompany,
          address: {
            street: formData.recipientStreet,
            houseNumber: formData.recipientHouseNo,
            flatNumber: formData.recipientFlatNo,
            city: formData.recipientCity,
            zipCode: formData.recipientZipCode,
            country: formData.recipientCountry,
          },
        },
        returnAddress: {
          firstName: formData.returnFirstName,
          lastName: formData.returnLastName,
          company: formData.returnCompany,
          address: {
            street: formData.returnStreet,
            houseNumber: formData.returnHouseNo,
            flatNumber: formData.returnFlatNo,
            city: formData.returnCity,
            zipCode: formData.returnZipCode,
          },
        },
        mailOptions: {
          type: formData.mailType,
          language: formData.language,
        },
        attachment: {
          fileName: uploadedFile.name,
          fileSize: uploadedFile.size,
          fileType: uploadedFile.type,
        },
      };

      console.log("API Request Body:", apiRequestBody);

      //alert("Form submitted successfully! (API call will be made here)");
      navigate("/letter-archive");
    }
  };

  return (
    <div className="new-letter">
      <div className="new-letter-header">
        <h1>Send new letter</h1>
        <div className="step-indicator">
          <div className="step active">1</div>
          <div className="step active">2</div>
          <div className={`step ${currentStep >= 3 ? "active" : ""}`}>3</div>
        </div>
      </div>

      <div className="new-letter-content">
        <div className="form-columns-three">
          {/* Left Column - Recipient */}
          <div className="form-column">
            {/* <h2>Recipient</h2> */}
            {/* <div className="form-group">
              <input
                type="text"
                name="recipientFirstName"
                placeholder="First name"
