import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./AdminOutgoing.css";
import { useTranslation } from "react-i18next";

// Mock data for outgoing letters
const mockLettersData = [
  {
    reference: "REF-2024-001",
    letterAddressee: "John Smith",
    status: "Sent",
    recipient: {
      street: "123 Main Street",
      houseNumber: "Apt 4B",
      zipCode: "10001",
      city: "New York",
      country: "USA"
    },
    returnAddress: {
      firstName: "Jane",
      lastName: "Doe",
      company: "Acme Corp",
      street: "456 Park Avenue",
      houseNumber: "Suite 100",
      zipCode: "10022",
      city: "New York"
    },
    mailOptions: {
      type: "Registered",
      language: "English"
    },
    notifySubject: "Letter Delivery Confirmation",
    notifyRecipients: "john@example.com"
  },
  {
    reference: "REF-2024-002",
    letterAddressee: "Sarah Johnson",
    status: "Outgoing",
    recipient: {
      street: "789 Oak Road",
      houseNumber: "House 5",
      zipCode: "75001",
      city: "Dallas",
      country: "USA"
    },
    returnAddress: {
      firstName: "Mike",
      lastName: "Wilson",
      company: "Tech Solutions",
      street: "999 Tech Lane",
      houseNumber: "Bldg A",
      zipCode: "75002",
      city: "Dallas"
    },
    mailOptions: {
      type: "Standard",
      language: "English"
    },
    notifySubject: "Delivery Status Update",
    notifyRecipients: "sarah@example.com"
  },
  {
    reference: "REF-2024-003",
    letterAddressee: "Robert Brown",
    status: "Pending",
    recipient: {
      street: "321 Elm Street",
      houseNumber: "Unit 12",
      zipCode: "60601",
      city: "Chicago",
      country: "USA"
    },
    returnAddress: {
      firstName: "Emily",
      lastName: "Davis",
      company: "Global Enterprises",
      street: "222 Commerce Drive",
      houseNumber: "Floor 5",
      zipCode: "60602",
      city: "Chicago"
    },
    mailOptions: {
      type: "Express",
      language: "English"
    },
    notifySubject: "Important Document Enclosed",
    notifyRecipients: "robert@example.com"
  },
  {
    reference: "REF-2024-004",
    letterAddressee: "Lisa Anderson",
    status: "Failed",
    recipient: {
      street: "654 Pine Avenue",
      houseNumber: "Apt 8",
      zipCode: "90001",
      city: "Los Angeles",
      country: "USA"
    },
    returnAddress: {
      firstName: "David",
      lastName: "Miller",
      company: "Premier Services",
      street: "111 Business Blvd",
      houseNumber: "Suite 200",
      zipCode: "90002",
      city: "Los Angeles"
    },
    mailOptions: {
      type: "Certified",
      language: "English"
    },
    notifySubject: "Delivery Attempt Failed",
    notifyRecipients: "lisa@example.com"
  },
  {
    reference: "REF-2024-005",
    letterAddressee: "Michael Chen",
    status: "Sent",
    recipient: {
      street: "987 Cedar Lane",
      houseNumber: "House 15",
      zipCode: "98001",
      city: "Seattle",
      country: "USA"
    },
    returnAddress: {
      firstName: "Karen",
      lastName: "Taylor",
      company: "Innovation Hub",
      street: "333 Innovation Way",
      houseNumber: "Bldg C",
      zipCode: "98002",
      city: "Seattle"
    },
    mailOptions: {
      type: "Standard",
      language: "English"
    },
    notifySubject: "Document Delivered",
    notifyRecipients: "michael@example.com"
  }
];

const AdminOutgoing = () => {
  const navigate = useNavigate();
  const { t } = useTranslation();
  const [letters, setLetters] = useState(mockLettersData);
  const [selectedLetter, setSelectedLetter] = useState(null);
  const [isPanelOpen, setIsPanelOpen] = useState(false);
  const [sortConfig, setSortConfig] = useState({ key: "reference", direction: "asc" });
  const [searchQuery, setSearchQuery] = useState("");

  // Handle letter selection
  const handleSelectLetter = (letter) => {
    setSelectedLetter(letter);
    setIsPanelOpen(true);
  };

  // Handle sort
  const handleSort = (key) => {
    let direction = "asc";
    if (sortConfig.key === key && sortConfig.direction === "asc") {
      direction = "desc";
    }
    setSortConfig({ key, direction });
  };

  // Filter letters by search
  const filteredLetters = letters.filter((letter) => {
    const query = searchQuery.toLowerCase();
    return (
      (letter.reference && letter.reference.toLowerCase().includes(query)) ||
      (letter.letterAddressee && letter.letterAddressee.toLowerCase().includes(query)) ||
      (letter.status && letter.status.toLowerCase().includes(query))
    );
  });

  // Sort letters
  const sortedLetters = [...filteredLetters].sort((a, b) => {
    const aValue = a[sortConfig.key] || "";
    const bValue = b[sortConfig.key] || "";

    if (sortConfig.direction === "asc") {
      return aValue < bValue ? -1 : 1;
    } else {
      return aValue > bValue ? -1 : 1;
    }
  });

  if (loading) {
    return (
      <div className="admin-outgoing">
        <div className="page-header">
          <h1>Admin - Outgoing Letters</h1>
        </div>
        <div style={{ padding: "20px", textAlign: "center" }}>
          <p>Loading outgoing letters...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="admin-outgoing">
        <div className="page-header">
          <h1>Admin - Outgoing Letters</h1>
        </div>
        <div style={{ padding: "20px", color: "red" }}>
          <p>Error: {error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-outgoing">
      <div className="page-header">
        <h1>Admin - Outgoing Letters</h1>
        <p className="subtitle">Manage and monitor outgoing letters</p>
      </div>

      <div className="search-section">
        <input
          type="text"
          placeholder="Search by reference, addressee, or status..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-input"
        />
        <span className="search-count">{sortedLetters.length} letters</span>
      </div>

      {sortedLetters.length === 0 ? (
        <div className="empty-state">
          <p>No outgoing letters found</p>
        </div>
      ) : (
        <div className="letters-table-container">
          <table className="letters-table">
            <thead>
              <tr>
                <th onClick={() => handleSort("reference")} className="sortable">
                  Reference {sortConfig.key === "reference" && (sortConfig.direction === "asc" ? "↑" : "↓")}
                </th>
                <th onClick={() => handleSort("letterAddressee")} className="sortable">
                  Addressee {sortConfig.key === "letterAddressee" && (sortConfig.direction === "asc" ? "↑" : "↓")}
                </th>
                <th onClick={() => handleSort("status")} className="sortable">
                  Status {sortConfig.key === "status" && (sortConfig.direction === "asc" ? "↑" : "↓")}
                </th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {sortedLetters.map((letter, index) => (
                <tr key={index} onClick={() => handleSelectLetter(letter)} className="clickable-row">
                  <td>{letter.reference || "-"}</td>
                  <td>{letter.letterAddressee || "-"}</td>
                  <td>
                    <span className={`status-badge status-${letter.status?.toLowerCase() || "pending"}`}>
                      {letter.status || "Pending"}
                    </span>
                  </td>
                  <td>
                    <button className="btn-view" onClick={(e) => {
                      e.stopPropagation();
                      handleSelectLetter(letter);
                    }}>
                      View
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Detail Panel */}
      {isPanelOpen && selectedLetter && (
        <div className="detail-panel-overlay" onClick={() => setIsPanelOpen(false)}>
          <div className="detail-panel" onClick={(e) => e.stopPropagation()}>
            <div className="panel-header">
              <h2>Letter Details</h2>
              <button className="close-button" onClick={() => setIsPanelOpen(false)}>×</button>
            </div>

            <div className="panel-content">
              <div className="detail-section">
                <h3>Reference Information</h3>
                <div className="detail-row">
                  <span className="label">Reference:</span>
                  <span className="value">{selectedLetter.reference}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Status:</span>
                  <span className={`status-badge status-${selectedLetter.status?.toLowerCase()}`}>
                    {selectedLetter.status}
                  </span>
                </div>
              </div>

              <div className="detail-section">
                <h3>Recipient Information</h3>
                <div className="detail-row">
                  <span className="label">Addressee:</span>
                  <span className="value">{selectedLetter.letterAddressee}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Address:</span>
                  <span className="value">
                    {selectedLetter.recipient?.street} {selectedLetter.recipient?.houseNumber}
                    <br />
                    {selectedLetter.recipient?.zipCode} {selectedLetter.recipient?.city}
                    <br />
                    {selectedLetter.recipient?.country}
                  </span>
                </div>
              </div>

              <div className="detail-section">
                <h3>Return Address</h3>
                <div className="detail-row">
                  <span className="label">From:</span>
                  <span className="value">
                    {selectedLetter.returnAddress?.firstName} {selectedLetter.returnAddress?.lastName}
                    <br />
                    {selectedLetter.returnAddress?.company}
                    <br />
                    {selectedLetter.returnAddress?.street} {selectedLetter.returnAddress?.houseNumber}
                    <br />
                    {selectedLetter.returnAddress?.zipCode} {selectedLetter.returnAddress?.city}
                  </span>
                </div>
              </div>

              <div className="detail-section">
                <h3>Mail Options</h3>
                <div className="detail-row">
                  <span className="label">Mail Type:</span>
                  <span className="value">{selectedLetter.mailOptions?.type || "-"}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Language:</span>
                  <span className="value">{selectedLetter.mailOptions?.language || "-"}</span>
                </div>
              </div>

              {selectedLetter.notifySubject && (
                <div className="detail-section">
                  <h3>Notification</h3>
                  <div className="detail-row">
                    <span className="label">Subject:</span>
                    <span className="value">{selectedLetter.notifySubject}</span>
                  </div>
                  <div className="detail-row">
                    <span className="label">Recipients:</span>
                    <span className="value">{selectedLetter.notifyRecipients}</span>
                  </div>
                </div>
              )}
            </div>

            <div className="panel-footer">
              <button className="btn-close" onClick={() => setIsPanelOpen(false)}>
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminOutgoing;
